import React, { useState } from 'react';

const CertificateRegistry = () => {
  const [tab, setTab] = useState('register');
  const [formData, setFormData] = useState({
    id: '',
    studentName: '',
    courseName: ''
  });
  const [searchId, setSearchId] = useState('');
  const [revokeId, setRevokeId] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
      // Web3 integration code would go here
      setResult('Certificate registered successfully!');
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setError('');
    try {
      // Web3 integration code would go here
      setResult({
        id: searchId,
        studentName: 'John Doe',
        courseName: 'Blockchain Development',
        issueDate: new Date().toLocaleDateString(),
        isValid: true
      });
    } catch (err) {
      setError(err.message);
    }
  };

  const handleRevoke = async (e) => {
    e.preventDefault();
    setError('');
    try {
      // Web3 integration code would go here
      setResult('Certificate revoked successfully!');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <div style={{ marginBottom: '20px' }}>
        <button 
          onClick={() => setTab('register')}
          style={{ marginRight: '10px' }}
        >
          Register Certificate
        </button>
        <button 
          onClick={() => setTab('search')}
          style={{ marginRight: '10px' }}
        >
          Search Certificate
        </button>
        <button 
          onClick={() => setTab('revoke')}
        >
          Revoke Certificate
        </button>
      </div>

      <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '4px' }}>
        <h2>
          {tab === 'register' && 'Register New Certificate'}
          {tab === 'search' && 'Search Certificate'}
          {tab === 'revoke' && 'Revoke Certificate'}
        </h2>

        {tab === 'register' && (
          <form onSubmit={handleRegister}>
            <div style={{ marginBottom: '10px' }}>
              <input
                name="id"
                placeholder="Certificate ID"
                value={formData.id}
                onChange={handleInputChange}
                required
                style={{ width: '100%', padding: '8px' }}
              />
            </div>
            <div style={{ marginBottom: '10px' }}>
              <input
                name="studentName"
                placeholder="Student Name"
                value={formData.studentName}
                onChange={handleInputChange}
                required
                style={{ width: '100%', padding: '8px' }}
              />
            </div>
            <div style={{ marginBottom: '10px' }}>
              <input
                name="courseName"
                placeholder="Course Name"
                value={formData.courseName}
                onChange={handleInputChange}
                required
                style={{ width: '100%', padding: '8px' }}
              />
            </div>
            <button type="submit" style={{ width: '100%', padding: '8px' }}>
              Register Certificate
            </button>
          </form>
        )}

        {tab === 'search' && (
          <form onSubmit={handleSearch}>
            <div style={{ marginBottom: '10px' }}>
              <input
                placeholder="Certificate ID"
                value={searchId}
                onChange={(e) => setSearchId(e.target.value)}
                required
                style={{ width: '100%', padding: '8px' }}
              />
            </div>
            <button type="submit" style={{ width: '100%', padding: '8px' }}>
              Search Certificate
            </button>
          </form>
        )}

        {tab === 'revoke' && (
          <form onSubmit={handleRevoke}>
            <div style={{ marginBottom: '10px' }}>
              <input
                placeholder="Certificate ID"
                value={revokeId}
                onChange={(e) => setRevokeId(e.target.value)}
                required
                style={{ width: '100%', padding: '8px' }}
              />
            </div>
            <button type="submit" style={{ width: '100%', padding: '8px' }}>
              Revoke Certificate
            </button>
          </form>
        )}

        {error && (
          <div style={{ 
            marginTop: '20px', 
            padding: '10px', 
            backgroundColor: '#ffebee', 
            color: '#c62828',
            borderRadius: '4px'
          }}>
            {error}
          </div>
        )}

        {result && (
          <div style={{ marginTop: '20px', padding: '10px', backgroundColor: '#f5f5f5', borderRadius: '4px' }}>
            {typeof result === 'string' ? (
              result
            ) : (
              <div>
                <p><strong>ID:</strong> {result.id}</p>
                <p><strong>Student:</strong> {result.studentName}</p>
                <p><strong>Course:</strong> {result.courseName}</p>
                <p><strong>Issue Date:</strong> {result.issueDate}</p>
                <p><strong>Status:</strong> {result.isValid ? 'Valid' : 'Revoked'}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default CertificateRegistry;
