import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Alert, AlertDescription } from '@/components/ui/alert';

const CertificateRegistry = () => {
  const [tab, setTab] = useState('register');
  const [formData, setFormData] = useState({
    id: '',
    studentName: '',
    courseName: ''
  });
  const [searchId, setSearchId] = useState('');
  const [revokeId, setRevokeId] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
      // Web3 integration code would go here
      setResult('Certificate registered successfully!');
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setError('');
    try {
      // Web3 integration code would go here
      setResult({
        id: searchId,
        studentName: 'John Doe',
        courseName: 'Blockchain Development',
        issueDate: new Date().toLocaleDateString(),
        isValid: true
      });
    } catch (err) {
      setError(err.message);
    }
  };

  const handleRevoke = async (e) => {
    e.preventDefault();
    setError('');
    try {
      // Web3 integration code would go here
      setResult('Certificate revoked successfully!');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <div className="mb-4 flex space-x-2">
        <Button 
          variant={tab === 'register' ? 'default' : 'outline'}
          onClick={() => setTab('register')}
        >
          Register Certificate
        </Button>
        <Button 
          variant={tab === 'search' ? 'default' : 'outline'}
          onClick={() => setTab('search')}
        >
          Search Certificate
        </Button>
        <Button 
          variant={tab === 'revoke' ? 'default' : 'outline'}
          onClick={() => setTab('revoke')}
        >
          Revoke Certificate
        </Button>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>
            {tab === 'register' && 'Register New Certificate'}
            {tab === 'search' && 'Search Certificate'}
            {tab === 'revoke' && 'Revoke Certificate'}
          </CardTitle>
        </CardHeader>
        <CardContent>
          {tab === 'register' && (
            <form onSubmit={handleRegister} className="space-y-4">
              <Input
                name="id"
                placeholder="Certificate ID"
                value={formData.id}
                onChange={handleInputChange}
                required
              />
              <Input
                name="studentName"
                placeholder="Student Name"
                value={formData.studentName}
                onChange={handleInputChange}
                required
              />
              <Input
                name="courseName"
                placeholder="Course Name"
                value={formData.courseName}
                onChange={handleInputChange}
                required
              />
              <Button type="submit" className="w-full">Register Certificate</Button>
            </form>
          )}

          {tab === 'search' && (
            <form onSubmit={handleSearch} className="space-y-4">
              <Input
                placeholder="Certificate ID"
                value={searchId}
                onChange={(e) => setSearchId(e.target.value)}
                required
              />
              <Button type="submit" className="w-full">Search Certificate</Button>
            </form>
          )}

          {tab === 'revoke' && (
            <form onSubmit={handleRevoke} className="space-y-4">
              <Input
                placeholder="Certificate ID"
                value={revokeId}
                onChange={(e) => setRevokeId(e.target.value)}
                required
              />
              <Button type="submit" className="w-full">Revoke Certificate</Button>
            </form>
          )}

          {error && (
            <Alert variant="destructive" className="mt-4">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          {result && (
            <div className="mt-4">
              {typeof result === 'string' ? (
                <Alert>
                  <AlertDescription>{result}</AlertDescription>
                </Alert>
              ) : (
                <div className="space-y-2">
                  <p><strong>ID:</strong> {result.id}</p>
                  <p><strong>Student:</strong> {result.studentName}</p>
                  <p><strong>Course:</strong> {result.courseName}</p>
                  <p><strong>Issue Date:</strong> {result.issueDate}</p>
                  <p><strong>Status:</strong> {result.isValid ? 'Valid' : 'Revoked'}</p>
                </div>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default CertificateRegistry;
